               CopyRoom(room, (i * 8) * width + j * 8, true);                            break;                        case Room.DropTop:                            room = DropTopList[Map._rnd.Next(0, DropTopList.Count)];                            CopyRoom(room, (i * 8) * width + j * 8, true);                            break;                        case Room.EndHall:                            room = HallEndList[Map._rnd.Next(0, HallEndList.Count)];                            CopyRoom(room, (i * 8) * width + j * 8, true);                            break;                        case Room.EndRightDeadEnd:                            room = RightEndList[Map._rnd.Next(0, RightEndList.Count)];                            CopyRoom(room, (i * 8) * width + j * 8, false);                            break;                        case Room.EndLeftDeadEnd:                            room = LeftEndList[Map._rnd.Next(0, LeftEndList.Count)];                            CopyRoom(room, (i * 8) * width + j * 8, false);                            break;                        case Room.Full:                            FillRoom((i * 8) * width + j * 8);                            break;                        default:                            Console.WriteLine(SourceMap.MapData[i * Map.width + j]);                            DebugRoom((i * 8) * width + j * 8);                            break;                    }                }            }        }        public void CopyRoom(Cell[] room, int start, bool flippable)        {            if (flippable && Map._rnd.Next(0, 2) == 1)            {                for (int i = 0; i < 9; i++)                {                    for (int j = 0; j < 9; j++)                    {                        if (GridData[start + (i * width) + j] == Cell.Empty)                        {                            GridData[start + (i * width) + j] = room[i * 9 + (8 - j)];                        }                    }                }            }            else            {                for (int i = 0; i < 9; i++)                {                    for (int j = 0; j < 9; j++)                    {                        if (GridData[start + (i * width) + j] == Cell.Empty)                        {                            GridData[start + (i * width) + j] = room[i * 9 + j];                        }                    }                }            }        }                public void FillRoom(int start)        {            for (int i = 0; i < 9; i++)            {                for (int j = 0; j < 9; j++)                {                    GridData[start + (i * width) + j] = Cell.Full;                }            }        }                public void DebugRoom(int start)        {            for (int i = 0; i < 9; i++)            {                for (int j = 0; j < 9; j++)                {                    GridData[start + (i * width) + j] = Cell.Debug;                }            }        }        public void Display()        {            for (int i = 0; i < height; i++)            {                for (int j = 0; j < width; j++)                {                    switch (GridData[i * width + j])                    {                        case Cell.Full:                            Console.Write("G");                            break;                        case Cell.Empty:                            Console.Write(" ");                            break;                        case Cell.Spawn:                            Console.Write("S");                            break;                        case Cell.Win:                            Console.Write("W");                            break;                        case Cell.Debug:                            Console.Write("$");                            break;                        case Cell.Lava:                            Console.Write("~");                            break;                        case Cell.Duck:                            Console.Write("D");                            break;                        case Cell.Decoration:                            Console.Write("*");                            break;                        default:                            Console.Write("?");                            break;                    }                    Console.Write(" ");                }                Console.WriteLine();            }        }    }}